<template>
  <div class="table">
    <div class="row header">
      <div class="name">Emotions</div>
      <div v-bind:class="computePrimaryClass(secondary)">% Before</div>
      <div v-bind:class="computePrimaryClass(secondary)">% Goal</div>
      <div v-bind:class="computeSecondaryClass(secondary)">
        % After
      </div>
    </div>
    <div class="row" v-for="(data, id) in emotions">
      <div class="name">{{ emotionNames[id] }}</div>
      <div v-bind:class="computePrimaryClass(secondary)">
        <input
          type="number"
          v-bind:value="data.before"
          placeholder="% Before"
          v-on:input="$emit('update', id, 'before', $event.target.value)"
        />
      </div>
      <div v-bind:class="computePrimaryClass(secondary)">
        <input
          type="number"
          v-bind:value="data.goal"
          placeholder="% Goal"
          v-on:input="$emit('update', id, 'goal', $event.target.value)"
        />
      </div>
      <div v-bind:class="computeSecondaryClass(secondary)">
        <input
          type="number"
          v-bind:value="data.after"
          placeholder="% After"
          v-on:input="$emit('update', id, 'after', $event.target.value)"
        />
      </div>
    </div>
    <div class="row" v-for="(data, id) in otherEmotions">
      <div class="other-options">
        <button v-on:click="$emit('add', id)">
          +
        </button>
        <button
          v-on:click="$emit('remove', id)"
          v-bind:disabled="otherEmotions.length < 2"
        >
          -
        </button>
        <input
          type="text"
          size="1"
          v-bind:value="data.name"
          placeholder="Other"
          v-on:input="$emit('update', id, 'name', $event.target.value)"
        />
      </div>
      <div v-bind:class="computePrimaryClass(secondary)">
        <input
          type="number"
          v-bind:value="data.before"
          placeholder="% Before"
          v-on:input="$emit('update', id, 'before', $event.target.value)"
        />
      </div>
      <div v-bind:class="computePrimaryClass(secondary)">
        <input
          type="number"
          v-bind:value="data.goal"
          placeholder="% Goal"
          v-on:input="$emit('update', id, 'goal', $event.target.value)"
        />
      </div>
      <div v-bind:class="computeSecondaryClass(secondary)">
        <input
          type="number"
          v-bind:value="data.after"
          placeholder="% After"
          v-on:input="$emit('update', id, 'after', $event.target.value)"
        />
      </div>
    </div>
  </div>
</template>

<script>
  import emotionNames from 'data/emotions';

  export default {
    name: 'mood-log-emotions',
    props: ['emotions', 'otherEmotions', 'secondary'],
    data() {
      return {
        emotionNames: emotionNames,
      };
    },
    methods: {
      onInput(e) {
        this.$emit('update', e.target.value);
      },
      computePrimaryClass(secondary) {
        return secondary ? 'no-screen' : undefined;
      },
      computeSecondaryClass(secondary) {
        return secondary ? undefined : 'no-screen';
      },
    }
  };
</script>

