<template>
  <div class="row">
    <div class="label"><div>Situation</div></div>
    <div class="input">
      <textarea
        rows="5"
        cols="70"
        v-bind:value="situation"
        v-on:input="onInput"
      ></textarea>
    </div>
  </div>
</template>

<script lang="ts">
  import Vue from 'vue';
  import Component from 'vue-class-component';

  @Component({
    name: 'mood-log-situation',
    props: {
      situation: String,
    },
  })
  export default class extends Vue {
    situation: string;
    onInput(e: Event) {
      this.$emit('update', (e.target as HTMLTextAreaElement).value);
    }
  };
</script>

<style lang='scss' scoped>
.row > div {
  text-align: center;
}
.label {
  font-weight: bold;
  padding-bottom: 0.5rem;
}
.input, textarea {
  max-width: 100%;
}
